.m-tooltipArrowList(@color, @size, @classes, @fillColor: false)
{
	.variations(@classes, @i: 1) when (@i <= length(@classes))
	{
		@variation: extract(@classes, @i);

		.m-tooltipArrow(@color, @size, ~'.@{variation}', @fillColor);

		.variations(@classes, @i + 1);
	}

	.variations(@classes);
}
